import React, { Component } from 'react'
import { auth } from './base'

class Login extends Component {
     constructor(props){
          super(props)

          this.email = null
          this.passwd = null

          this.state = {
               isLogged
          }

          this.handleLogin = this.handleLogin.bind(this)
     }
     handleLogin(){
          //pega login e senha
          console.log('login', this.email.value, this.passwd.value)
          auth
               .signInWithEmailAndPassword(this.email.value, this.passwd.value)
               .then((user) =>{
                    //Se logou entra aqui
                    console.log('logged in', user)
               })
               .catch(error => {
                    //Login incorreto entra aqui
                    console.log('error', error)
               })
     }
     render(){
          return(
               <div>
                    <input type='email' ref={ref => this.email = ref} />
                    <input type='passwd' ref={ref => this.passwd = ref}/>
                    <button onClick={this.handleLogin}> Entrar </button>
               </div>
          )
     }
}
export default Login
